import React, { Component } from "react"
import Form from "../components/Form"

import Results from "./Results";
import Saved from "./Saved";
import API from "../utils/API";
import Header from "../components/Header"
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";

class Search extends Component {
  state = {
    searchTopic: "",
    searchStart: "",
    searchEnd: "",
    query: "",
    results: [],
    savedArticles: []
  };

  componentDidMount() {
    this.loadArticles();
  }

  searchNYT = (topic = "Seattle", start = "20180201", end = "20180202") => {
    API.search(topic, start, end)
      .then(res => {
        console.log("res.data.data", res.data.data);
        this.setState({ results: res.data.data })
      })
      .catch(err => console.log(err))
  };

  handleInputChange = event => {
    const name = event.target.name;
    const value = event.target.value;
    this.setState({
      [name]: value
    });
  };

  // When the form is submitted, search the Giphy API for `this.state.search`
  handleFormSubmit = event => {
    event.preventDefault();
    this.searchNYT(this.state.searchTopic, this.state.searchStart, this.state.searchEnd);
  };

  saveArticle = event => {
    console.log("inside saveArticle");
    API.saveThisArticle({
      id: this.state.id,
      title: this.state.title,
      url: this.state.url,
      date: this.state.date,
      time: this.state.time
    })
      .then(res => this.loadArticles())
      .catch(err => console.log(err));
  };

  loadArticles = () => {
    API.getSavedArticles(() => console.log("inside API call")
    )
      .then(res => {
        console.log("res", res);
        return (
          this.setState({ savedArticles: res.data }))
      }
      )
      .catch(err => console.log(err));
  };

  removeArticle = id => {
    API.deleteArticle(id)
      .then(res => this.loadArticles())
      .catch(err => console.log(err))
  };

  render() {
    return (
      <Container fluid>

        <Col size="md-12">
          <Row>
            <Header
              linkSearch={this.query} />
            <h3 className="title">Search</h3>
            <Row>
            <Form
              label="topic"
              labelName="Topic"
              search={this.state.searchTopic}
              searchName="searchTopic"
              handleInputChange={this.handleInputChange}
            />
            </Row>
            <Form
              label="beginDate"
              labelName="Start Year"
              search={this.state.searchStart}
              searchName="searchStart"
              handleInputChange={this.handleInputChange}
              placeholder="YYYYMMDD"
            />
            <Form
              label="endDate"
              labelName="End Year"
              search={this.state.searchEnd}
              searchName="searchEnd"
              handleInputChange={this.handleInputChange}
              placeholder="YYYYMMDD"
            />
            <button
              onClick={() => this.handleFormSubmit}
              className="btn btn-primary mt-3"
              id="btnSearch"
            >
              Search
        </button>
          </Row>
          <br></br><br></br><br></br>
          <Row>
            <h3 className="title">Results</h3>
            {this.state.results.map(result => (
              <Results
                id={result.id}
                key={result.id}
                title={result.title}
                url={result.url}
                date={result.date}
                time={result.time}
                save={this.saveArticle}
              />
            ))
            }
          </Row>
          <Row>
            <h3 className="title">Saved</h3>
            {this.state.savedArticles.map(saved => (
              <Saved
                id={saved.id}
                key={saved.id}
                title={saved.title}
                url={saved.url}
                date={saved.date}
                time={saved.time}
                remove={this.removeArticle}
              />
            ))
            }
          </Row>
        </Col>
      </Container>
    );
  }
}

export default Search;
